// Kilowatt App Color Palette (Monochromatic + Complementary)
// Usage: import colors from './assets/colors';
// NOTE: App is optimized for larger text for accessibility.

const colors = {
  primary: {
    main: '#C82828',       // Dominant deep red
    light: '#E85555',      // Lighter red
    dark: '#A01729',       // Darker red
  },
  secondary: {
    main: '#E68228',       // Orange
    light: '#F0A078',      // Light orange/peach
    dark: '#DC5028',       // Dark orange
  },
  background: '#FFFFFF',   // Light background (white)
  accent1: '#E68228',      // Orange
  accent2: '#E66E28',      // Orange-brown
  accent3: '#E65A28',      // Deep orange
  accent4: '#DC5028',      // Red-orange
  accent5: '#F0A078',      // Peach
  accent6: '#F0A028',      // Golden orange
  text: '#222222',         // Main text (dark)
  textLight: '#FFFFFF',    // Light text for dark backgrounds
  border: '#E0E0E0',       // Subtle border
};

export default colors;
